 46→    set { dataSource.didMoveItemHandler = newValue }
 47→  }
 48→
 49→  /// Applies the given snapshot, computing and animating the minimal diff.
 50→  ///
 51→  /// When `Item` conforms to ``ContentEquatable``, items whose identity matches
 52→  /// but whose content has changed are automatically marked for reconfiguration.
 53→  public func apply(_ snapshot: Snapshot, animatingDifferences: Bool = true) async {
 54→    var snapshot = snapshot
 55→    autoReconfigure(old: dataSource.snapshot(), new: &snapshot)
 56→    await dataSource.apply(snapshot, animatingDifferences: animatingDifferences)
 57→  }
 58→
 59→  /// Replaces the current data without diffing, calling `reloadData()` on the collection view.
 60→  public func applyUsingReloadData(_ snapshot: Snapshot) async {
 61→    await dataSource.applySnapshotUsingReloadData(snapshot)
 62→  }
