 64→  /// Applies an array of ``SectionModel`` values, building a snapshot automatically.
 65→  public func apply(_ sections: [SectionModel<SectionID, Item>], animatingDifferences: Bool = true) async {
 66→    var snapshot = Snapshot()
 67→    for section in sections {
 68→      snapshot.appendSections([section.id])
 69→      snapshot.appendItems(section.items, toSection: section.id)
 70→    }
 71→    await apply(snapshot, animatingDifferences: animatingDifferences)
 72→  }
