 75→  public func apply(
 76→    animatingDifferences: Bool = true,
 77→    @SnapshotBuilder<SectionID, Item> content: () -> [SnapshotSection<SectionID, Item>]
 78→  ) async {
 79→    let sections = content()
 80→    var snapshot = Snapshot()
 81→    for section in sections {
 82→      snapshot.appendSections([section.id])
 83→      snapshot.appendItems(section.items, toSection: section.id)
 84→    }
 85→    await apply(snapshot, animatingDifferences: animatingDifferences)
 86→  }
