154→      guard let newCE = newItem as? any ContentEquatable else {
155→        assertionFailure("Item passed type-level ContentEquatable check but failed instance cast")
156→        return true
157→      }
158→      return newCE.isContentEqualTypeErased(to: oldItem)
159→    }
160→
161→    if !toReconfigure.isEmpty {
162→      new.reconfigureItems(toReconfigure)
163→    }
164→  }
