// ABOUTME: Shared protocol for common collection view convenience APIs.
// ABOUTME: Provides default implementations for selection, scrolling, and computed properties.
import ListKit
import UIKit

// MARK: - ListConfigurable

/// A protocol that provides common convenience APIs for list configurations.
///
/// `ListConfigurable` eliminates API duplication across ``SimpleList``, ``GroupedList``,
/// and ``OutlineList`` by deriving convenience members from just four requirements:
/// the collection view, a snapshot accessor, and item/index-path lookups.
///
/// ## Conforming Types
///
/// Conformance requires four members (all already public on each configuration):
/// - `collectionView` — the underlying `UICollectionView`
/// - `snapshot()` — returns a copy of the current diffable data source snapshot
/// - `itemIdentifier(for:)` — resolves an `IndexPath` to an item
/// - `indexPath(for:)` — resolves an item to an `IndexPath`
@MainActor
public protocol ListConfigurable: AnyObject {
  associatedtype SectionID: Hashable & Sendable
  associatedtype Item: CellViewModel

  /// The underlying collection view managed by this list configuration.
  var collectionView: UICollectionView { get }

  /// Returns a copy of the current snapshot.
  func snapshot() -> DiffableDataSourceSnapshot<SectionID, Item>

  /// Returns the item at the given index path, or `nil` if out of bounds.
  func itemIdentifier(for indexPath: IndexPath) -> Item?

  /// Returns the index path for the specified item, or `nil` if not found.
  func indexPath(for item: Item) -> IndexPath?
}

// MARK: - Default Implementations

extension ListConfigurable {

  /// A view displayed behind the list content.
  ///
  /// The view is placed as the collection view's `backgroundView`. Visibility is managed
  /// automatically by the list configuration after each data mutation.
  public var backgroundView: UIView? {
    get { collectionView.backgroundView }
    set { collectionView.backgroundView = newValue }
  }

  /// Whether the list allows multiple simultaneous selections.
  ///
  /// When `true`, tapping an item does not automatically deselect other items.
  public var allowsMultipleSelection: Bool {
    get { collectionView.allowsMultipleSelection }
    set { collectionView.allowsMultipleSelection = newValue }
  }

  /// Whether selection is allowed during editing mode.
  public var allowsSelectionDuringEditing: Bool {
    get { collectionView.allowsSelectionDuringEditing }
    set { collectionView.allowsSelectionDuringEditing = newValue }
  }

  /// Whether multiple selection is allowed during editing mode.
  public var allowsMultipleSelectionDuringEditing: Bool {
    get { collectionView.allowsMultipleSelectionDuringEditing }
    set { collectionView.allowsMultipleSelectionDuringEditing = newValue }
  }

  /// Whether the list is in editing mode.
  ///
  /// When `true`, cells may display editing controls such as delete and reorder accessories.
  public var isEditing: Bool {
    get { collectionView.isEditing }
    set { collectionView.isEditing = newValue }
  }

  /// The total number of items across all sections.
  public var numberOfItems: Int {
    (0..<collectionView.numberOfSections).reduce(0) { $0 + collectionView.numberOfItems(inSection: $1) }
  }

  /// The number of sections in the list.
  public var numberOfSections: Int {
    collectionView.numberOfSections
  }

  /// The currently selected items, derived from the collection view's selected index paths.
  public var selectedItems: [Item] {
    (collectionView.indexPathsForSelectedItems ?? []).compactMap { itemIdentifier(for: $0) }
  }

  /// Programmatically selects the specified item.
  ///
  /// - Parameters:
  ///   - item: The item to select.
  ///   - scrollPosition: Where to scroll after selecting. Pass `[]` for no scrolling.
  ///   - animated: Whether the selection should be animated.
  /// - Returns: `true` if the item was found and selected, `false` if not present.
  @discardableResult
  public func selectItem(
    _ item: Item,
    at scrollPosition: UICollectionView.ScrollPosition = [],
    animated: Bool = true
  ) -> Bool {
    guard let indexPath = indexPath(for: item) else { return false }
    collectionView.selectItem(at: indexPath, animated: animated, scrollPosition: scrollPosition)
    return true
  }

  /// Programmatically deselects the specified item.
  ///
  /// - Parameters:
  ///   - item: The item to deselect.
  ///   - animated: Whether the deselection should be animated.
  /// - Returns: `true` if the item was found and deselected, `false` if not present.
  @discardableResult
  public func deselectItem(_ item: Item, animated: Bool = true) -> Bool {
    guard let indexPath = indexPath(for: item) else { return false }
    collectionView.deselectItem(at: indexPath, animated: animated)
    return true
  }

  /// Returns whether the specified item is currently selected.
  public func isSelected(_ item: Item) -> Bool {
    guard let indexPath = indexPath(for: item) else { return false }
    return collectionView.indexPathsForSelectedItems?.contains(indexPath) ?? false
  }

  /// Deselects all currently selected items.
  ///
  /// - Parameter animated: Whether the deselection should be animated.
  public func deselectAll(animated: Bool = true) {
    for indexPath in collectionView.indexPathsForSelectedItems ?? [] {
      collectionView.deselectItem(at: indexPath, animated: animated)
    }
  }

  /// Scrolls to the top of the list.
  public func scrollToTop(animated: Bool = true) {
    let topOffset = CGPoint(x: 0, y: -collectionView.adjustedContentInset.top)
    collectionView.setContentOffset(topOffset, animated: animated)
  }

  /// Scrolls to the bottom of the list.
  ///
  /// - Note: This method reads `contentSize` and `bounds`, which are zero before the
  ///   collection view has performed layout. Call after the view appears or after
  ///   `layoutIfNeeded()` to ensure correct behavior.
  public func scrollToBottom(animated: Bool = true) {
    let contentHeight = collectionView.contentSize.height
    let frameHeight = collectionView.bounds.height
    let bottomInset = collectionView.adjustedContentInset.bottom
    guard contentHeight > frameHeight else { return }
    let bottomOffset = CGPoint(x: 0, y: contentHeight - frameHeight + bottomInset)
    collectionView.setContentOffset(bottomOffset, animated: animated)
  }

  /// Programmatically scrolls to the specified item.
  ///
  /// - Returns: `true` if the item was found in the current snapshot and the scroll was
  ///   initiated, `false` if the item is not present.
  @discardableResult
  public func scrollToItem(
    _ item: Item,
    at scrollPosition: UICollectionView.ScrollPosition = .centeredVertically,
    animated: Bool = true
  ) -> Bool {
    guard let indexPath = indexPath(for: item) else { return false }
    collectionView.scrollToItem(at: indexPath, at: scrollPosition, animated: animated)
    return true
  }
}

// ABOUTME: Shared data source algorithms for content-change detection and section snapshot flattening.
// ABOUTME: Used by ListDataSource and MixedListDataSource.
import ListKit

/// Computes items that need reconfiguration by comparing old and new snapshots.
///
/// For items that match by identity (`Hashable`/`Equatable`) in both snapshots, calls
/// `isContentEqual` to determine whether visible content has changed. Returns items
/// from the new snapshot whose content differs from their counterparts in the old snapshot.
///
/// - Parameters:
///   - old: The previous snapshot before the update.
///   - new: The new snapshot being applied.
///   - isContentEqual: Closure that returns `true` when two identity-matching items have equal content.
/// - Returns: Items from `new` whose content differs from their counterpart in `old`.
func computeItemsToReconfigure<SectionID: Hashable & Sendable, Item: Hashable & Sendable>(
  old: DiffableDataSourceSnapshot<SectionID, Item>,
  new: DiffableDataSourceSnapshot<SectionID, Item>,
  isContentEqual: (Item, Item) -> Bool
) -> [Item] {
  let oldItems = old.itemIdentifiers
  guard !oldItems.isEmpty else { return [] }

  var oldLookup = [Item: Item]()
  oldLookup.reserveCapacity(oldItems.count)
  for item in oldItems {
    oldLookup[item] = item
  }

  var toReconfigure = [Item]()
  for newItem in new.itemIdentifiers {
    guard let oldItem = oldLookup[newItem] else { continue }
    if !isContentEqual(newItem, oldItem) {
      toReconfigure.append(newItem)
    }
  }
  return toReconfigure
}

/// Flattens a section snapshot's visible items into a full snapshot, replacing the target section's items.
///
/// Returns a new snapshot with the section's items replaced by the section snapshot's
/// ``DiffableDataSourceSectionSnapshot/visibleItems``. Hierarchical parent–child structure
/// is not preserved in the returned snapshot.
///
/// - Parameters:
///   - sectionSnapshot: The hierarchical section snapshot to flatten.
///   - section: The section whose items should be replaced.
///   - snapshot: The current full snapshot.
/// - Returns: A new snapshot with flattened items, or `nil` if the section does not exist.
func flattenSectionSnapshot<SectionID: Hashable & Sendable, Item: Hashable & Sendable>(
  _ sectionSnapshot: DiffableDataSourceSectionSnapshot<Item>,
  intoSection section: SectionID,
  of snapshot: DiffableDataSourceSnapshot<SectionID, Item>
) -> DiffableDataSourceSnapshot<SectionID, Item>? {
  var newSnapshot = snapshot
  guard newSnapshot.sectionIdentifiers.contains(section) else {
    assertionFailure("Cannot apply section snapshot to non-existent section \(section)")
    return nil
  }
  let oldItems = newSnapshot.itemIdentifiers(inSection: section)
  newSnapshot.deleteItems(oldItems)
  let visibleItems = sectionSnapshot.visibleItems
  if !visibleItems.isEmpty {
    newSnapshot.appendItems(visibleItems, toSection: section)
  }
  return newSnapshot
}

// ABOUTME: Single-section flat list configuration backed by UICollectionView.
// ABOUTME: Simplest list type -- just provide items and handlers.
import ListKit
import UIKit

/// A single-section flat list backed by a `UICollectionView`.
///
/// `SimpleList` is the easiest way to display a homogeneous list. It manages layout,
/// data source, and delegation internally — you just provide items and handlers.
///
/// ## Layout Configuration
///
/// Layout properties (`appearance`, `showsSeparators`, `separatorColor`, `backgroundColor`,
/// `headerTopPadding`) are set during initialization and cannot be changed afterward.
/// This is a UIKit limitation — `UICollectionLayoutListConfiguration` is snapshot-immutable
/// once the layout is created. Dynamic properties like ``allowsMultipleSelection`` and
/// ``isEditing`` can be changed at any time.
///
/// ## Closure Callbacks
///
/// Callback properties (`onSelect`, `onDelete`, `trailingSwipeActionsProvider`, etc.) are
/// stored as strong references. If the closure captures the list's owner (e.g. a view
/// controller), use `[weak self]` to avoid a retain cycle.
///
/// ```swift
/// let list = SimpleList<ContactItem>()
/// list.onSelect = { item in print(item) }
/// await list.setItems(contacts)
/// ```
@MainActor
public final class SimpleList<Item: CellViewModel>: NSObject, UICollectionViewDelegate, ListConfigurable {

  // MARK: Lifecycle

  deinit {
    applyTask?.cancel()
    refreshManager.cancel()
  }

  /// Creates a simple list with the specified list appearance.
  ///
  /// - Parameters:
  ///   - appearance: The visual style of the list (e.g., `.plain`, `.insetGrouped`, `.sidebar`).
  ///   - showsSeparators: Whether separators are shown between rows.
  ///   - separatorColor: A global tint color applied to all item separators. Per-item
  ///     customization via ``separatorHandler`` takes precedence.
  ///   - backgroundColor: An optional background color for the list. When `nil`, the system default is used.
  ///   - headerTopPadding: Extra padding above each section header. When `nil`, the system default is used.
  ///   - selfSizingInvalidation: Controls how the collection view handles self-sizing invalidation.
  ///     Use `.disabled` when cell content changes frequently (e.g. streaming text) to avoid animated
  ///     resize bouncing — the list will perform non-animated reconfigure passes instead.
  public init(
    appearance: UICollectionLayoutListConfiguration.Appearance = .plain,
    showsSeparators: Bool = true,
    separatorColor: UIColor? = nil,
    backgroundColor: UIColor? = nil,
    headerTopPadding: CGFloat? = nil,
    selfSizingInvalidation: UICollectionView.SelfSizingInvalidation = .enabled
  ) {
    let bridge = ListConfigurationBridge<Int, Item>()
    self.bridge = bridge
    bridge.setDefaultSeparatorColor(separatorColor)

    var config = UICollectionLayoutListConfiguration(appearance: appearance)
    config.showsSeparators = showsSeparators
    if let backgroundColor {
      config.backgroundColor = backgroundColor
    }
    if let headerTopPadding {
      config.headerTopPadding = headerTopPadding
    }
    bridge.configure(&config)
    let layout = UICollectionViewCompositionalLayout.list(using: config)

    collectionView = UICollectionView(frame: .zero, collectionViewLayout: layout)
    collectionView.selfSizingInvalidation = selfSizingInvalidation
    dataSource = ListDataSource(collectionView: collectionView)
    super.init()
    collectionView.delegate = self
    refreshManager.attach(to: collectionView)

    bridge.setDataSource(dataSource)
    // trailingSwipeActionsProvider takes precedence; onDelete is the fallback.
    bridge.setTrailingProvider { [weak self] item in
      guard let self else { return nil }
      if let config = trailingSwipeActionsProvider?(item) {
        return config
      }
      guard let onDelete else { return nil }
      let action = UIContextualAction(style: .destructive, title: String(localized: "Delete")) { _, _, completion in
        onDelete(item)
        completion(true)
      }
      return UISwipeActionsConfiguration(actions: [action])
    }
    bridge.setLeadingProvider { [weak self] item in self?.leadingSwipeActionsProvider?(item) }
  }

  // MARK: Public

  /// A convenience alias for the snapshot type used by this list.
  public typealias Snapshot = DiffableDataSourceSnapshot<Int, Item>

  /// The underlying collection view. Add this to your view hierarchy.
  public let collectionView: UICollectionView
  /// Called when the user taps an item.
  public var onSelect: (@MainActor (Item) -> Void)?
  /// Called when the user deselects an item (relevant when `allowsMultipleSelection` is enabled).
  public var onDeselect: (@MainActor (Item) -> Void)?

  /// Called when the user swipe-deletes an item. When set and ``trailingSwipeActionsProvider``
  /// is `nil`, a trailing destructive "Delete" swipe action is provided automatically.
  ///
  /// - Important: The caller is responsible for removing the item from the data source
  ///   after this callback fires. The list does not automatically mutate its snapshot.
  ///
  /// - Note: The swipe action always completes as "performed" regardless of whether the
  ///   underlying operation succeeds. Ensure your handler updates the data source to
  ///   reflect the actual result.
  public var onDelete: (@MainActor (Item) -> Void)?

  /// Closure that returns trailing swipe actions for a given item.
  public var trailingSwipeActionsProvider: (@MainActor (Item) -> UISwipeActionsConfiguration?)?
  /// Closure that returns leading swipe actions for a given item.
  public var leadingSwipeActionsProvider: (@MainActor (Item) -> UISwipeActionsConfiguration?)?
  /// Closure that returns a context menu configuration for a given item.
  public var contextMenuProvider: (@MainActor (Item) -> UIContextMenuConfiguration?)?

  /// Optional closure called before an item is selected. Return `false` to prevent selection.
  ///
  /// Use this to implement conditional selection (e.g. disabling taps on certain items):
  /// ```swift
  /// list.shouldSelect = { item in !item.isDisabled }
  /// ```
  public var shouldSelect: (@MainActor (Item) -> Bool)?

  /// An optional delegate that receives `UIScrollViewDelegate` callbacks from the underlying
  /// collection view's scroll view.
  ///
  /// Use this to track scroll position, detect user-initiated drags, or respond to deceleration
  /// events without replacing the collection view's delegate (which the list manages internally).
  ///
  /// ```swift
  /// list.scrollViewDelegate = myScrollHandler
  /// ```
  public weak var scrollViewDelegate: UIScrollViewDelegate?

  /// Per-item separator customization handler.
  ///
  /// Called for each item before display. Return a modified configuration to customize
  /// separator appearance (color, insets, visibility) on a per-item basis.
  ///
  /// ```swift
  /// list.separatorHandler = { item, defaultConfig in
  ///     var config = defaultConfig
  ///     config.color = .systemRed
  ///     config.bottomSeparatorInsets.leading = 16
  ///     return config
  /// }
  /// ```
  public var separatorHandler: (@MainActor (Item, UIListSeparatorConfiguration) -> UIListSeparatorConfiguration)? {
    didSet { bridge.setSeparatorProvider(separatorHandler) }
  }

  /// An async closure invoked on pull-to-refresh. The refresh control is dismissed
  /// automatically when the closure returns.
  ///
  /// ```swift
  /// list.onRefresh = {
  ///     let items = try? await api.fetchItems()
  ///     await list.setItems(items ?? [])
  /// }
  /// ```
  public var onRefresh: (@MainActor () async -> Void)? {
    didSet { refreshManager.onRefresh = onRefresh }
  }

  /// Called when the user reorders an item via drag-and-drop.
  /// The list updates its internal snapshot automatically; use this to persist the new order.
  /// Setting this enables the reorder interaction on the collection view.
  public var onMove: (@MainActor (_ source: IndexPath, _ destination: IndexPath) -> Void)? {
    didSet { configureReorderIfNeeded() }
  }

  /// Optional closure that determines whether a specific item can be reordered.
  ///
  /// When set, this is called for each item when drag begins. Return `false` to prevent
  /// an item from being dragged. When `nil`, all items are moveable (if `onMove` is set).
  public var canMoveItemProvider: (@MainActor (Item) -> Bool)? {
    didSet { configureReorderIfNeeded() }
  }

  /// The current items in the list, derived from the snapshot.
  ///
  /// Returns an empty array if the list has not been populated yet.
  public var items: [Item] {
    dataSource.snapshot().itemIdentifiers
  }

  /// Replaces the list's items, computing and animating the diff.
  ///
  /// Cancels any previously queued apply so only the most recent snapshot is applied,
  /// and supports cooperative cancellation from the calling task.
  public func setItems(_ items: [Item], animatingDifferences: Bool = true) async {
    applyTask?.cancel()
    let previousTask = applyTask
    let task = Task { [weak self] in
      _ = await previousTask?.value
      guard !Task.isCancelled, let self else { return }
      var snapshot = DiffableDataSourceSnapshot<Int, Item>()
      snapshot.appendSections([0])
      snapshot.appendItems(items, toSection: 0)
      await dataSource.apply(snapshot, animatingDifferences: animatingDifferences)
      collectionView.backgroundView?.isHidden = !items.isEmpty
    }
    applyTask = task
    await withTaskCancellationHandler {
      await task.value
    } onCancel: {
      task.cancel()
    }
  }

  /// Replaces the list's items using the ``ItemsBuilder`` result builder DSL.
  public func setItems(animatingDifferences: Bool = true, @ItemsBuilder<Item> content: () -> [Item]) async {
    await setItems(content(), animatingDifferences: animatingDifferences)
  }

  /// Returns a copy of the current snapshot.
  public func snapshot() -> DiffableDataSourceSnapshot<Int, Item> {
    dataSource.snapshot()
  }

  /// Returns the item at the given index path, or `nil` if out of bounds.
  public func itemIdentifier(for indexPath: IndexPath) -> Item? {
    bridge.itemIdentifier(for: indexPath)
  }

  /// Returns the index path for the specified item, or `nil` if not found.
  public func indexPath(for item: Item) -> IndexPath? {
    bridge.indexPath(for: item)
  }

  public func collectionView(
    _: UICollectionView,
    shouldSelectItemAt indexPath: IndexPath
  ) -> Bool {
    bridge.handleShouldSelect(at: indexPath, shouldSelect: shouldSelect)
  }

  public func collectionView(_ collectionView: UICollectionView, didSelectItemAt indexPath: IndexPath) {
    bridge.handleDidSelect(in: collectionView, at: indexPath, onSelect: onSelect)
  }

  public func collectionView(_: UICollectionView, didDeselectItemAt indexPath: IndexPath) {
    bridge.handleDidDeselect(at: indexPath, onDeselect: onDeselect)
  }

  public func collectionView(
    _: UICollectionView,
    contextMenuConfigurationForItemAt indexPath: IndexPath,
    point _: CGPoint
  ) -> UIContextMenuConfiguration? {
    bridge.handleContextMenu(at: indexPath, provider: contextMenuProvider)
  }

  // These @objc methods cannot be provided by a protocol extension (Swift protocol extensions
  // don't participate in Objective-C dispatch), so they are duplicated across SimpleList,
  // GroupedList, and OutlineList by necessity.

  public func scrollViewDidScroll(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewDidScroll?(scrollView)
  }

  public func scrollViewWillBeginDragging(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewWillBeginDragging?(scrollView)
  }

  public func scrollViewWillEndDragging(
    _ scrollView: UIScrollView,
    withVelocity velocity: CGPoint,
    targetContentOffset: UnsafeMutablePointer<CGPoint>
  ) {
    scrollViewDelegate?.scrollViewWillEndDragging?(scrollView, withVelocity: velocity, targetContentOffset: targetContentOffset)
  }

  public func scrollViewDidEndDragging(_ scrollView: UIScrollView, willDecelerate decelerate: Bool) {
    scrollViewDelegate?.scrollViewDidEndDragging?(scrollView, willDecelerate: decelerate)
  }

  public func scrollViewWillBeginDecelerating(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewWillBeginDecelerating?(scrollView)
  }

  public func scrollViewDidEndDecelerating(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewDidEndDecelerating?(scrollView)
  }

  public func scrollViewDidEndScrollingAnimation(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewDidEndScrollingAnimation?(scrollView)
  }

  public func scrollViewShouldScrollToTop(_ scrollView: UIScrollView) -> Bool {
    scrollViewDelegate?.scrollViewShouldScrollToTop?(scrollView) ?? true
  }

  public func scrollViewDidScrollToTop(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewDidScrollToTop?(scrollView)
  }

  public func scrollViewDidChangeAdjustedContentInset(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewDidChangeAdjustedContentInset?(scrollView)
  }

  // MARK: Private

  private let dataSource: ListDataSource<Int, Item>
  private let bridge: ListConfigurationBridge<Int, Item>
  private let refreshManager = RefreshControlManager()
  private var applyTask: Task<Void, Never>?

  private func configureReorderIfNeeded() {
    if onMove != nil {
      collectionView.dragInteractionEnabled = true
      if let canMoveItemProvider {
        dataSource.canMoveItemHandler = { [weak self] indexPath in
          guard let item = self?.bridge.itemIdentifier(for: indexPath) else { return false }
          return canMoveItemProvider(item)
        }
      } else {
        dataSource.canMoveItemHandler = { _ in true }
      }
      dataSource.didMoveItemHandler = { [weak self] source, dest in
        self?.onMove?(source, dest)
      }
    } else {
      collectionView.dragInteractionEnabled = false
      dataSource.canMoveItemHandler = nil
      dataSource.didMoveItemHandler = nil
    }
  }

}

// ABOUTME: Multi-section grouped list configuration with headers and footers.
// ABOUTME: Manages UICollectionView layout, data source, and delegate internally.
import ListKit
import UIKit

/// A multi-section list with headers and footers, backed by a `UICollectionView`.
///
/// `GroupedList` manages an inset-grouped layout with supplementary header/footer views
/// automatically. Provide ``SectionModel`` values to populate sections.
///
/// ## Layout Configuration
///
/// Layout properties (`appearance`, `showsSeparators`, `headerMode`, `footerMode`,
/// `separatorColor`, `backgroundColor`, `headerTopPadding`) are set during initialization
/// and cannot be changed afterward. This is a UIKit limitation —
/// `UICollectionLayoutListConfiguration` is snapshot-immutable once the layout is created.
/// Dynamic properties like ``allowsMultipleSelection`` and ``isEditing`` can be changed
/// at any time.
///
/// ## Closure Callbacks
///
/// Callback properties (`onSelect`, `onDelete`, `trailingSwipeActionsProvider`, etc.) are
/// stored as strong references. If the closure captures the list's owner (e.g. a view
/// controller), use `[weak self]` to avoid a retain cycle.
///
/// ```swift
/// let list = GroupedList<String, ContactItem>()
/// await list.setSections([
///     SectionModel(id: "friends", items: friends, header: "Friends"),
/// ])
/// ```
@MainActor
public final class GroupedList<SectionID: Hashable & Sendable, Item: CellViewModel>: NSObject, UICollectionViewDelegate,
  ListConfigurable
{

  // MARK: Lifecycle

  deinit {
    applyTask?.cancel()
    refreshManager.cancel()
  }

  /// Creates a grouped list with the specified list appearance.
  ///
  /// - Parameters:
  ///   - appearance: The visual style of the list (e.g., `.insetGrouped`, `.plain`, `.sidebar`).
  ///   - showsSeparators: Whether separators are shown between rows.
  ///   - headerMode: How section headers are displayed. Defaults to `.supplementary`.
  ///   - footerMode: How section footers are displayed. Defaults to `.supplementary`.
  ///   - separatorColor: A global tint color applied to all item separators. Per-item
  ///     customization via ``separatorHandler`` takes precedence.
  ///   - backgroundColor: An optional background color for the list. When `nil`, the system default is used.
  ///   - headerTopPadding: Extra padding above each section header. When `nil`, the system default is used.
  public init(
    appearance: UICollectionLayoutListConfiguration.Appearance = .insetGrouped,
    showsSeparators: Bool = true,
    headerMode: UICollectionLayoutListConfiguration.HeaderMode = .supplementary,
    footerMode: UICollectionLayoutListConfiguration.FooterMode = .supplementary,
    separatorColor: UIColor? = nil,
    backgroundColor: UIColor? = nil,
    headerTopPadding: CGFloat? = nil
  ) {
    let bridge = ListConfigurationBridge<SectionID, Item>()
    self.bridge = bridge
    bridge.setDefaultSeparatorColor(separatorColor)

    var config = UICollectionLayoutListConfiguration(appearance: appearance)
    config.headerMode = headerMode
    config.footerMode = footerMode
    config.showsSeparators = showsSeparators
    if let backgroundColor {
      config.backgroundColor = backgroundColor
    }
    if let headerTopPadding {
      config.headerTopPadding = headerTopPadding
    }
    bridge.configure(&config)
    let layout = UICollectionViewCompositionalLayout.list(using: config)

    collectionView = UICollectionView(frame: .zero, collectionViewLayout: layout)
    dataSource = ListDataSource(collectionView: collectionView)
    super.init()
    collectionView.delegate = self
    refreshManager.attach(to: collectionView)
    // Register supplementary views only when at least one mode is .supplementary.
    // Both header and footer registrations are created together; the layout only
    // requests the kinds matching its configuration, so unused registrations are inert.
    if headerMode == .supplementary || footerMode == .supplementary {
      setupSupplementaryViews()
    }

    bridge.setDataSource(dataSource)
    // trailingSwipeActionsProvider takes precedence; onDelete is the fallback.
    bridge.setTrailingProvider { [weak self] item in
      guard let self else { return nil }
      if let config = trailingSwipeActionsProvider?(item) {
        return config
      }
      guard let onDelete else { return nil }
      let action = UIContextualAction(style: .destructive, title: String(localized: "Delete")) { _, _, completion in
        onDelete(item)
        completion(true)
      }
      return UISwipeActionsConfiguration(actions: [action])
    }
    bridge.setLeadingProvider { [weak self] item in self?.leadingSwipeActionsProvider?(item) }
  }

  // MARK: Public

  /// A convenience alias for the snapshot type used by this list.
  public typealias Snapshot = DiffableDataSourceSnapshot<SectionID, Item>

  /// The underlying collection view. Add this to your view hierarchy.
  public let collectionView: UICollectionView
  /// Called when the user taps an item.
  public var onSelect: (@MainActor (Item) -> Void)?
  /// Called when the user deselects an item (relevant when `allowsMultipleSelection` is enabled).
  public var onDeselect: (@MainActor (Item) -> Void)?

  /// Called when the user swipe-deletes an item. When set and ``trailingSwipeActionsProvider``
  /// is `nil`, a trailing destructive "Delete" swipe action is provided automatically.
  ///
  /// - Important: The caller is responsible for removing the item from the data source
  ///   after this callback fires. The list does not automatically mutate its snapshot.
  ///
  /// - Note: The swipe action always completes as "performed" regardless of whether the
  ///   underlying operation succeeds. Ensure your handler updates the data source to
  ///   reflect the actual result.
  public var onDelete: (@MainActor (Item) -> Void)?

  /// Closure that returns trailing swipe actions for a given item.
  public var trailingSwipeActionsProvider: (@MainActor (Item) -> UISwipeActionsConfiguration?)?
  /// Closure that returns leading swipe actions for a given item.
  public var leadingSwipeActionsProvider: (@MainActor (Item) -> UISwipeActionsConfiguration?)?
  /// Closure that returns a context menu configuration for a given item.
  public var contextMenuProvider: (@MainActor (Item) -> UIContextMenuConfiguration?)?

  /// Optional closure called before an item is selected. Return `false` to prevent selection.
  ///
  /// Use this to implement conditional selection (e.g. disabling taps on certain items):
  /// ```swift
  /// list.shouldSelect = { item in !item.isDisabled }
  /// ```
  public var shouldSelect: (@MainActor (Item) -> Bool)?

  /// Closure that returns a custom content configuration for a section header.
  ///
  /// When set, this takes precedence over the text-based header from ``SectionModel``.
  /// Return `nil` to fall back to the text header for a specific section.
  ///
  /// ```swift
  /// list.headerContentProvider = { sectionID in
  ///     var config = UIListContentConfiguration.prominentInsetGroupedHeader()
  ///     config.text = sectionID
  ///     config.image = UIImage(systemName: "star")
  ///     return config
  /// }
  /// ```
  public var headerContentProvider: (@MainActor (SectionID) -> UIContentConfiguration?)?

  /// Closure that returns a custom content configuration for a section footer.
  ///
  /// When set, this takes precedence over the text-based footer from ``SectionModel``.
  /// Return `nil` to fall back to the text footer for a specific section.
  public var footerContentProvider: (@MainActor (SectionID) -> UIContentConfiguration?)?

  /// An optional delegate that receives `UIScrollViewDelegate` callbacks from the underlying
  /// collection view's scroll view.
  ///
  /// Use this to track scroll position, detect user-initiated drags, or respond to deceleration
  /// events without replacing the collection view's delegate (which the list manages internally).
  public weak var scrollViewDelegate: UIScrollViewDelegate?

  /// Per-item separator customization handler.
  ///
  /// Called for each item before display. Return a modified configuration to customize
  /// separator appearance (color, insets, visibility) on a per-item basis.
  ///
  /// ```swift
  /// list.separatorHandler = { item, defaultConfig in
  ///     var config = defaultConfig
  ///     config.color = .systemRed
  ///     config.bottomSeparatorInsets.leading = 16
  ///     return config
  /// }
  /// ```
  public var separatorHandler: (@MainActor (Item, UIListSeparatorConfiguration) -> UIListSeparatorConfiguration)? {
    didSet { bridge.setSeparatorProvider(separatorHandler) }
  }

  /// An async closure invoked on pull-to-refresh. The refresh control is dismissed
  /// automatically when the closure returns.
  ///
  /// ```swift
  /// list.onRefresh = {
  ///     let sections = try? await api.fetchSections()
  ///     await list.setSections(sections ?? [])
  /// }
  /// ```
  public var onRefresh: (@MainActor () async -> Void)? {
    didSet { refreshManager.onRefresh = onRefresh }
  }

  /// Called when the user reorders an item via drag-and-drop.
  /// The list updates its internal snapshot automatically; use this to persist the new order.
  /// Setting this enables the reorder interaction on the collection view.
  public var onMove: (@MainActor (_ source: IndexPath, _ destination: IndexPath) -> Void)? {
    didSet { configureReorderIfNeeded() }
  }

  /// Optional closure that determines whether a specific item can be reordered.
  ///
  /// When set, this is called for each item when drag begins. Return `false` to prevent
  /// an item from being dragged. When `nil`, all items are moveable (if `onMove` is set).
  public var canMoveItemProvider: (@MainActor (Item) -> Bool)? {
    didSet { configureReorderIfNeeded() }
  }

  /// Replaces all sections, computing and animating the diff.
  ///
  /// Cancels any previously queued apply so only the most recent snapshot is applied,
  /// and supports cooperative cancellation from the calling task.
  public func setSections(_ sections: [SectionModel<SectionID, Item>], animatingDifferences: Bool = true) async {
    applyTask?.cancel()
    let previousTask = applyTask
    let task = Task { [weak self] in
      _ = await previousTask?.value
      guard !Task.isCancelled, let self else { return }
      var newHeaders = [SectionID: String]()
      var newFooters = [SectionID: String]()
      for section in sections {
        if let header = section.header {
          newHeaders[section.id] = header
        }
        if let footer = section.footer {
          newFooters[section.id] = footer
        }
      }
      // Merge new values so both old and new sections have valid headers during animation
      sectionHeaders.merge(newHeaders) { _, new in new }
      sectionFooters.merge(newFooters) { _, new in new }
      await dataSource.apply(sections, animatingDifferences: animatingDifferences)
      // Trim to only current sections after apply completes
      sectionHeaders = newHeaders
      sectionFooters = newFooters
      collectionView.backgroundView?.isHidden = !sections.allSatisfy(\.items.isEmpty)
    }
    applyTask = task
    await withTaskCancellationHandler {
      await task.value
    } onCancel: {
      task.cancel()
    }
  }

  /// Replaces all sections using the ``SnapshotBuilder`` result builder DSL.
  ///
  /// ``SnapshotSection`` supports optional `header` and `footer` text that maps directly
  /// to ``SectionModel`` headers/footers. For fully custom supplementary content, set
  /// ``headerContentProvider`` / ``footerContentProvider`` instead.
  ///
  /// ```swift
  /// await list.setSections {
  ///     SnapshotSection("favorites", header: "Favorites") {
  ///         favoriteItem1
  ///         favoriteItem2
  ///     }
  ///     SnapshotSection("recent", header: "Recent") {
  ///         recentItem1
  ///     }
  /// }
  /// ```
  public func setSections(
    animatingDifferences: Bool = true,
    @SnapshotBuilder<SectionID, Item> content: () -> [SnapshotSection<SectionID, Item>]
  ) async {
    let sections = content().map { section in
      SectionModel(id: section.id, items: section.items, header: section.header, footer: section.footer)
    }
    await setSections(sections, animatingDifferences: animatingDifferences)
  }

  /// Returns a copy of the current snapshot.
  public func snapshot() -> DiffableDataSourceSnapshot<SectionID, Item> {
    dataSource.snapshot()
  }

  /// Returns the item at the given index path, or `nil` if out of bounds.
  public func itemIdentifier(for indexPath: IndexPath) -> Item? {
    bridge.itemIdentifier(for: indexPath)
  }

  /// Returns the index path for the specified item, or `nil` if not found.
  public func indexPath(for item: Item) -> IndexPath? {
    bridge.indexPath(for: item)
  }

  /// Returns the section identifier at the given section index, or `nil` if out of bounds.
  public func sectionIdentifier(for index: Int) -> SectionID? {
    bridge.sectionIdentifier(for: index)
  }

  /// Returns the index of the specified section identifier, or `nil` if not found.
  public func index(for sectionIdentifier: SectionID) -> Int? {
    bridge.index(for: sectionIdentifier)
  }

  /// Returns the items in the specified section, or `nil` if the section is not found.
  public func items(in section: SectionID) -> [Item]? {
    let snap = dataSource.snapshot()
    guard snap.sectionIdentifiers.contains(section) else { return nil }
    return snap.itemIdentifiers(inSection: section)
  }

  public func collectionView(
    _: UICollectionView,
    shouldSelectItemAt indexPath: IndexPath
  ) -> Bool {
    bridge.handleShouldSelect(at: indexPath, shouldSelect: shouldSelect)
  }

  public func collectionView(_ collectionView: UICollectionView, didSelectItemAt indexPath: IndexPath) {
    bridge.handleDidSelect(in: collectionView, at: indexPath, onSelect: onSelect)
  }

  public func collectionView(_: UICollectionView, didDeselectItemAt indexPath: IndexPath) {
    bridge.handleDidDeselect(at: indexPath, onDeselect: onDeselect)
  }

  public func collectionView(
    _: UICollectionView,
    contextMenuConfigurationForItemAt indexPath: IndexPath,
    point _: CGPoint
  ) -> UIContextMenuConfiguration? {
    bridge.handleContextMenu(at: indexPath, provider: contextMenuProvider)
  }

  // These @objc methods cannot be provided by a protocol extension (Swift protocol extensions
  // don't participate in Objective-C dispatch), so they are duplicated across SimpleList,
  // GroupedList, and OutlineList by necessity.

  public func scrollViewDidScroll(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewDidScroll?(scrollView)
  }

  public func scrollViewWillBeginDragging(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewWillBeginDragging?(scrollView)
  }

  public func scrollViewWillEndDragging(
    _ scrollView: UIScrollView,
    withVelocity velocity: CGPoint,
    targetContentOffset: UnsafeMutablePointer<CGPoint>
  ) {
    scrollViewDelegate?.scrollViewWillEndDragging?(scrollView, withVelocity: velocity, targetContentOffset: targetContentOffset)
  }

  public func scrollViewDidEndDragging(_ scrollView: UIScrollView, willDecelerate decelerate: Bool) {
    scrollViewDelegate?.scrollViewDidEndDragging?(scrollView, willDecelerate: decelerate)
  }

  public func scrollViewWillBeginDecelerating(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewWillBeginDecelerating?(scrollView)
  }

  public func scrollViewDidEndDecelerating(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewDidEndDecelerating?(scrollView)
  }

  public func scrollViewDidEndScrollingAnimation(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewDidEndScrollingAnimation?(scrollView)
  }

  public func scrollViewShouldScrollToTop(_ scrollView: UIScrollView) -> Bool {
    scrollViewDelegate?.scrollViewShouldScrollToTop?(scrollView) ?? true
  }

  public func scrollViewDidScrollToTop(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewDidScrollToTop?(scrollView)
  }

  public func scrollViewDidChangeAdjustedContentInset(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewDidChangeAdjustedContentInset?(scrollView)
  }

  // MARK: Private

  private let dataSource: ListDataSource<SectionID, Item>
  private let bridge: ListConfigurationBridge<SectionID, Item>
  private let refreshManager = RefreshControlManager()
  private var sectionHeaders = [SectionID: String]()
  private var sectionFooters = [SectionID: String]()
  private var applyTask: Task<Void, Never>?

  private func configureReorderIfNeeded() {
    if onMove != nil {
      collectionView.dragInteractionEnabled = true
      if let canMoveItemProvider {
        dataSource.canMoveItemHandler = { [weak self] indexPath in
          guard let item = self?.bridge.itemIdentifier(for: indexPath) else { return false }
          return canMoveItemProvider(item)
        }
      } else {
        dataSource.canMoveItemHandler = { _ in true }
      }
      dataSource.didMoveItemHandler = { [weak self] source, dest in
        self?.onMove?(source, dest)
      }
    } else {
      collectionView.dragInteractionEnabled = false
      dataSource.canMoveItemHandler = nil
      dataSource.didMoveItemHandler = nil
    }
  }

  private func setupSupplementaryViews() {
    let headerRegistration = UICollectionView.SupplementaryRegistration<UICollectionViewListCell>(
      elementKind: UICollectionView.elementKindSectionHeader
    ) { [weak self] supplementaryView, _, indexPath in
      guard let self else { return }
      guard let sectionID = dataSource.sectionIdentifier(for: indexPath.section) else {
        assertionFailure("Section index \(indexPath.section) out of bounds")
        return
      }
      if let contentConfig = headerContentProvider?(sectionID) {
        supplementaryView.contentConfiguration = contentConfig
      } else {
        var content = UIListContentConfiguration.groupedHeader()
        content.text = sectionHeaders[sectionID]
        supplementaryView.contentConfiguration = content
      }
    }

    let footerRegistration = UICollectionView.SupplementaryRegistration<UICollectionViewListCell>(
      elementKind: UICollectionView.elementKindSectionFooter
    ) { [weak self] supplementaryView, _, indexPath in
      guard let self else { return }
      guard let sectionID = dataSource.sectionIdentifier(for: indexPath.section) else {
        assertionFailure("Section index \(indexPath.section) out of bounds")
        return
      }
      if let contentConfig = footerContentProvider?(sectionID) {
        supplementaryView.contentConfiguration = contentConfig
      } else {
        var content = UIListContentConfiguration.groupedFooter()
        content.text = sectionFooters[sectionID]
        supplementaryView.contentConfiguration = content
      }
    }

    dataSource.supplementaryViewProvider = { collectionView, kind, indexPath in
      switch kind {
      case UICollectionView.elementKindSectionHeader:
        return collectionView.dequeueConfiguredReusableSupplementary(using: headerRegistration, for: indexPath)
      case UICollectionView.elementKindSectionFooter:
        return collectionView.dequeueConfiguredReusableSupplementary(using: footerRegistration, for: indexPath)
      default:
        assertionFailure("Unexpected supplementary view kind: \(kind)")
        return nil
      }
    }
  }
}

// ABOUTME: Hierarchical outline list with expand/collapse via section snapshots.
// ABOUTME: Defines OutlineItem (tree node) and OutlineList (UICollectionView config).
import ListKit
import UIKit

// MARK: - OutlineItem

/// A node in a tree structure used by ``OutlineList`` to represent hierarchical data.
///
/// Each `OutlineItem` wraps a value and optionally has children. The `isExpanded` flag
/// controls whether children are visible when the outline is rendered.
public struct OutlineItem<Item: Hashable & Sendable>: Sendable, Equatable, Identifiable {

  // MARK: Lifecycle

  /// Creates an outline item with optional children and expansion state.
  public init(item: Item, children: [OutlineItem<Item>] = [], isExpanded: Bool = false) {
    self.item = item
    self.children = children
    self.isExpanded = isExpanded
  }

  // MARK: Public

  /// The data value for this node.
  public let item: Item
  /// The child nodes. Empty for leaf items.
  public let children: [OutlineItem<Item>]
  /// Whether this node's children should be visible.
  public let isExpanded: Bool

  /// The identity of this outline item, derived from its ``item`` value.
  public var id: Item {
    item
  }

  /// Recursively transforms every `item` in this tree using the given closure.
  public func mapItems<T: Hashable & Sendable>(_ transform: (Item) -> T) -> OutlineItem<T> {
    OutlineItem<T>(
      item: transform(item),
      children: children.map { $0.mapItems(transform) },
      isExpanded: isExpanded
    )
  }
}

// MARK: - OutlineList

/// A hierarchical outline list backed by a `UICollectionView` with section snapshots.
///
/// `OutlineList` uses ``DiffableDataSourceSectionSnapshot`` under the hood to model
/// parent–child relationships. Items can be expanded or collapsed.
///
/// - Note: Unlike ``SimpleList`` and ``GroupedList``, `OutlineList` does not support
///   drag-and-drop reordering (`onMove`) due to the complexity of hierarchical move
///   constraints. Use a flat list if reordering is required.
///
/// ## Layout Configuration
///
/// Layout properties (`appearance`, `showsSeparators`, `separatorColor`, `backgroundColor`,
/// `headerTopPadding`) are set during initialization and cannot be changed afterward.
/// This is a UIKit limitation — `UICollectionLayoutListConfiguration` is snapshot-immutable
/// once the layout is created. Dynamic properties like ``allowsMultipleSelection`` and
/// ``isEditing`` can be changed at any time.
///
/// ## Closure Callbacks
///
/// Callback properties (`onSelect`, `onDelete`, `trailingSwipeActionsProvider`, etc.) are
/// stored as strong references. If the closure captures the list's owner (e.g. a view
/// controller), use `[weak self]` to avoid a retain cycle.
///
/// ```swift
/// let list = OutlineList<FileItem>()
/// await list.setItems([
///     OutlineItem(item: folder, children: [
///         OutlineItem(item: file)
///     ], isExpanded: true)
/// ])
/// ```
@MainActor
public final class OutlineList<Item: CellViewModel>: NSObject, UICollectionViewDelegate, ListConfigurable {

  // MARK: Lifecycle

  deinit {
    applyTask?.cancel()
    refreshManager.cancel()
  }

  /// Creates an outline list with the specified list appearance.
  ///
  /// - Parameters:
  ///   - appearance: The visual style of the list (e.g., `.sidebar`, `.plain`, `.insetGrouped`).
  ///   - showsSeparators: Whether separators are shown between rows.
  ///   - separatorColor: A global tint color applied to all item separators. Per-item
  ///     customization via ``separatorHandler`` takes precedence.
  ///   - backgroundColor: An optional background color for the list. When `nil`, the system default is used.
  ///   - headerTopPadding: Extra padding above each section header. When `nil`, the system default is used.
  public init(
    appearance: UICollectionLayoutListConfiguration.Appearance = .sidebar,
    showsSeparators: Bool = true,
    separatorColor: UIColor? = nil,
    backgroundColor: UIColor? = nil,
    headerTopPadding: CGFloat? = nil
  ) {
    let bridge = ListConfigurationBridge<Int, Item>()
    self.bridge = bridge
    bridge.setDefaultSeparatorColor(separatorColor)

    var config = UICollectionLayoutListConfiguration(appearance: appearance)
    config.showsSeparators = showsSeparators
    if let backgroundColor {
      config.backgroundColor = backgroundColor
    }
    if let headerTopPadding {
      config.headerTopPadding = headerTopPadding
    }
    bridge.configure(&config)
    let layout = UICollectionViewCompositionalLayout.list(using: config)

    collectionView = UICollectionView(frame: .zero, collectionViewLayout: layout)
    dataSource = ListDataSource(collectionView: collectionView)
    super.init()
    collectionView.delegate = self
    refreshManager.attach(to: collectionView)

    bridge.setDataSource(dataSource)
    // trailingSwipeActionsProvider takes precedence; onDelete is the fallback.
    bridge.setTrailingProvider { [weak self] item in
      guard let self else { return nil }
      if let config = trailingSwipeActionsProvider?(item) {
        return config
      }
      guard let onDelete else { return nil }
      let action = UIContextualAction(style: .destructive, title: String(localized: "Delete")) { _, _, completion in
        onDelete(item)
        completion(true)
      }
      return UISwipeActionsConfiguration(actions: [action])
    }
    bridge.setLeadingProvider { [weak self] item in self?.leadingSwipeActionsProvider?(item) }
  }

  // MARK: Public

  /// A convenience alias for the snapshot type used by this list.
  public typealias Snapshot = DiffableDataSourceSnapshot<Int, Item>

  /// The underlying collection view. Add this to your view hierarchy.
  public let collectionView: UICollectionView
  /// Called when the user taps an item.
  public var onSelect: (@MainActor (Item) -> Void)?
  /// Called when the user deselects an item (relevant when `allowsMultipleSelection` is enabled).
  public var onDeselect: (@MainActor (Item) -> Void)?

  /// Called when the user swipe-deletes an item. When set and ``trailingSwipeActionsProvider``
  /// is `nil`, a trailing destructive "Delete" swipe action is provided automatically.
  ///
  /// - Important: The caller is responsible for removing the item from the data source
  ///   after this callback fires. The list does not automatically mutate its snapshot.
  ///
  /// - Note: The swipe action always completes as "performed" regardless of whether the
  ///   underlying operation succeeds. Ensure your handler updates the data source to
  ///   reflect the actual result.
  public var onDelete: (@MainActor (Item) -> Void)?

  /// Closure that returns trailing swipe actions for a given item.
  public var trailingSwipeActionsProvider: (@MainActor (Item) -> UISwipeActionsConfiguration?)?
  /// Closure that returns leading swipe actions for a given item.
  public var leadingSwipeActionsProvider: (@MainActor (Item) -> UISwipeActionsConfiguration?)?
  /// Closure that returns a context menu configuration for a given item.
  public var contextMenuProvider: (@MainActor (Item) -> UIContextMenuConfiguration?)?

  /// Optional closure called before an item is selected. Return `false` to prevent selection.
  ///
  /// Use this to implement conditional selection (e.g. disabling taps on certain items):
  /// ```swift
  /// list.shouldSelect = { item in !item.isDisabled }
  /// ```
  public var shouldSelect: (@MainActor (Item) -> Bool)?

  /// An optional delegate that receives `UIScrollViewDelegate` callbacks from the underlying
  /// collection view's scroll view.
  ///
  /// Use this to track scroll position, detect user-initiated drags, or respond to deceleration
  /// events without replacing the collection view's delegate (which the list manages internally).
  public weak var scrollViewDelegate: UIScrollViewDelegate?

  /// An async closure invoked on pull-to-refresh. The refresh control is dismissed
  /// automatically when the closure returns.
  ///
  /// ```swift
  /// list.onRefresh = {
  ///     let items = try? await api.fetchTree()
  ///     await list.setItems(items ?? [])
  /// }
  /// ```
  public var onRefresh: (@MainActor () async -> Void)? {
    didSet { refreshManager.onRefresh = onRefresh }
  }

  /// Per-item separator customization handler.
  ///
  /// Called for each item before display. Return a modified configuration to customize
  /// separator appearance (color, insets, visibility) on a per-item basis.
  ///
  /// ```swift
  /// list.separatorHandler = { item, defaultConfig in
  ///     var config = defaultConfig
  ///     config.color = .systemRed
  ///     config.bottomSeparatorInsets.leading = 16
  ///     return config
  /// }
  /// ```
  public var separatorHandler: (@MainActor (Item, UIListSeparatorConfiguration) -> UIListSeparatorConfiguration)? {
    didSet { bridge.setSeparatorProvider(separatorHandler) }
  }

  /// All items in the outline, including items hidden under collapsed parents.
  public var allItems: [Item] {
    currentSectionSnapshot?.items ?? []
  }

  /// The currently visible items (excludes items under collapsed parents).
  public var visibleItems: [Item] {
    currentSectionSnapshot?.visibleItems ?? []
  }

  /// The root-level items in the outline.
  public var rootItems: [Item] {
    currentSectionSnapshot?.rootItems ?? []
  }

  /// Returns the parent of the specified item, or `nil` if it is a root item.
  public func parent(of item: Item) -> Item? {
    currentSectionSnapshot?.parent(of: item)
  }

  /// Returns the nesting level of the specified item (0 for root items).
  public func level(of item: Item) -> Int {
    currentSectionSnapshot?.level(of: item) ?? 0
  }

  /// Replaces the outline's tree, computing and animating the diff.
  ///
  /// Uses `apply(_:to:animatingDifferences:)` with a section snapshot so UIKit handles
  /// hierarchy natively — expand/collapse animations and optimized subtree diffing come
  /// for free instead of forcing a full flat diff on every update.
  ///
  /// Cancels any previously queued apply so only the most recent snapshot is applied,
  /// and supports cooperative cancellation from the calling task.
  public func setItems(_ items: [OutlineItem<Item>], animatingDifferences: Bool = true) async {
    applyTask?.cancel()
    let previousTask = applyTask
    let task = Task { [weak self] in
      _ = await previousTask?.value
      guard !Task.isCancelled, let self else { return }

      // Ensure the section exists before applying the section snapshot.
      var mainSnapshot = dataSource.snapshot()
      if mainSnapshot.numberOfSections == 0 {
        mainSnapshot.appendSections([0])
        await dataSource.apply(mainSnapshot, animatingDifferences: false)
        guard !Task.isCancelled else { return }
      }

      var sectionSnapshot = DiffableDataSourceSectionSnapshot<Item>()
      appendItems(items, to: nil, in: &sectionSnapshot)
      currentSectionSnapshot = sectionSnapshot
      await dataSource.apply(sectionSnapshot, to: 0, animatingDifferences: animatingDifferences)
      collectionView.backgroundView?.isHidden = !items.isEmpty
    }
    applyTask = task
    await withTaskCancellationHandler {
      await task.value
    } onCancel: {
      task.cancel()
    }
  }

  /// Programmatically expands the specified item, making its children visible.
  ///
  /// This method participates in the same task serialization chain as ``setItems(_:animatingDifferences:)``
  /// to prevent interleaved applies.
  ///
  /// - Parameters:
  ///   - item: The item to expand. Must be present in the current outline and have children.
  ///   - animated: Whether to animate the expansion. Defaults to `true`.
  public func expand(_ item: Item, animated: Bool = true) async {
    applyTask?.cancel()
    let previousTask = applyTask
    let task = Task { [weak self] in
      _ = await previousTask?.value
      guard !Task.isCancelled, let self else { return }
      guard var sectionSnapshot = currentSectionSnapshot, sectionSnapshot.contains(item) else { return }
      sectionSnapshot.expand([item])
      currentSectionSnapshot = sectionSnapshot
      await dataSource.apply(sectionSnapshot, to: 0, animatingDifferences: animated)
    }
    applyTask = task
    await withTaskCancellationHandler { await task.value } onCancel: { task.cancel() }
  }

  /// Programmatically collapses the specified item, hiding its children.
  ///
  /// This method participates in the same task serialization chain as ``setItems(_:animatingDifferences:)``
  /// to prevent interleaved applies.
  ///
  /// - Parameters:
  ///   - item: The item to collapse. Must be present in the current outline and have children.
  ///   - animated: Whether to animate the collapse. Defaults to `true`.
  public func collapse(_ item: Item, animated: Bool = true) async {
    applyTask?.cancel()
    let previousTask = applyTask
    let task = Task { [weak self] in
      _ = await previousTask?.value
      guard !Task.isCancelled, let self else { return }
      guard var sectionSnapshot = currentSectionSnapshot, sectionSnapshot.contains(item) else { return }
      sectionSnapshot.collapse([item])
      currentSectionSnapshot = sectionSnapshot
      await dataSource.apply(sectionSnapshot, to: 0, animatingDifferences: animated)
    }
    applyTask = task
    await withTaskCancellationHandler { await task.value } onCancel: { task.cancel() }
  }

  /// Returns whether the specified item is currently expanded.
  ///
  /// - Parameter item: The item to check.
  /// - Returns: `true` if the item is expanded, `false` if collapsed or not found.
  public func isExpanded(_ item: Item) -> Bool {
    currentSectionSnapshot?.isExpanded(item) ?? false
  }

  /// Replaces the outline's tree using the ``OutlineItemBuilder`` result builder DSL.
  ///
  /// ```swift
  /// await list.setItems {
  ///     OutlineItem(item: folder, isExpanded: true) {
  ///         OutlineItem(item: fileA)
  ///         OutlineItem(item: fileB)
  ///     }
  ///     OutlineItem(item: standalone)
  /// }
  /// ```
  public func setItems(
    animatingDifferences: Bool = true,
    @OutlineItemBuilder<Item> content: () -> [OutlineItem<Item>]
  ) async {
    await setItems(content(), animatingDifferences: animatingDifferences)
  }

  /// Returns a copy of the current snapshot.
  public func snapshot() -> DiffableDataSourceSnapshot<Int, Item> {
    dataSource.snapshot()
  }

  /// Returns the item at the given index path, or `nil` if out of bounds.
  public func itemIdentifier(for indexPath: IndexPath) -> Item? {
    bridge.itemIdentifier(for: indexPath)
  }

  /// Returns the index path for the specified item, or `nil` if not found.
  public func indexPath(for item: Item) -> IndexPath? {
    bridge.indexPath(for: item)
  }

  public func collectionView(
    _: UICollectionView,
    shouldSelectItemAt indexPath: IndexPath
  ) -> Bool {
    bridge.handleShouldSelect(at: indexPath, shouldSelect: shouldSelect)
  }

  public func collectionView(_ collectionView: UICollectionView, didSelectItemAt indexPath: IndexPath) {
    bridge.handleDidSelect(in: collectionView, at: indexPath, onSelect: onSelect)
  }

  public func collectionView(_: UICollectionView, didDeselectItemAt indexPath: IndexPath) {
    bridge.handleDidDeselect(at: indexPath, onDeselect: onDeselect)
  }

  public func collectionView(
    _: UICollectionView,
    contextMenuConfigurationForItemAt indexPath: IndexPath,
    point _: CGPoint
  ) -> UIContextMenuConfiguration? {
    bridge.handleContextMenu(at: indexPath, provider: contextMenuProvider)
  }

  // These @objc methods cannot be provided by a protocol extension (Swift protocol extensions
  // don't participate in Objective-C dispatch), so they are duplicated across SimpleList,
  // GroupedList, and OutlineList by necessity.

  public func scrollViewDidScroll(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewDidScroll?(scrollView)
  }

  public func scrollViewWillBeginDragging(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewWillBeginDragging?(scrollView)
  }

  public func scrollViewWillEndDragging(
    _ scrollView: UIScrollView,
    withVelocity velocity: CGPoint,
    targetContentOffset: UnsafeMutablePointer<CGPoint>
  ) {
    scrollViewDelegate?.scrollViewWillEndDragging?(scrollView, withVelocity: velocity, targetContentOffset: targetContentOffset)
  }

  public func scrollViewDidEndDragging(_ scrollView: UIScrollView, willDecelerate decelerate: Bool) {
    scrollViewDelegate?.scrollViewDidEndDragging?(scrollView, willDecelerate: decelerate)
  }

  public func scrollViewWillBeginDecelerating(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewWillBeginDecelerating?(scrollView)
  }

  public func scrollViewDidEndDecelerating(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewDidEndDecelerating?(scrollView)
  }

  public func scrollViewDidEndScrollingAnimation(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewDidEndScrollingAnimation?(scrollView)
  }

  public func scrollViewShouldScrollToTop(_ scrollView: UIScrollView) -> Bool {
    scrollViewDelegate?.scrollViewShouldScrollToTop?(scrollView) ?? true
  }

  public func scrollViewDidScrollToTop(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewDidScrollToTop?(scrollView)
  }

  public func scrollViewDidChangeAdjustedContentInset(_ scrollView: UIScrollView) {
    scrollViewDelegate?.scrollViewDidChangeAdjustedContentInset?(scrollView)
  }

  // MARK: Private

  private let dataSource: ListDataSource<Int, Item>
  private let bridge: ListConfigurationBridge<Int, Item>
  private let refreshManager = RefreshControlManager()
  private var applyTask: Task<Void, Never>?
  private var currentSectionSnapshot: DiffableDataSourceSectionSnapshot<Item>?

  private func appendItems(
    _ outlineItems: [OutlineItem<Item>],
    to parent: Item?,
    in sectionSnapshot: inout DiffableDataSourceSectionSnapshot<Item>
  ) {
    let items = outlineItems.map(\.item)
    sectionSnapshot.append(items, to: parent)

    for outlineItem in outlineItems {
      if outlineItem.isExpanded {
        sectionSnapshot.expand([outlineItem.item])
      }
      if !outlineItem.children.isEmpty {
        appendItems(outlineItem.children, to: outlineItem.item, in: &sectionSnapshot)
      }
    }
  }
}

