    let registrar = CellRegistrar<Item>()
    self.registrar = registrar
    dataSource = CollectionViewDiffableDataSource(collectionView: collectionView) { cv, indexPath, item in
      registrar.dequeue(from: cv, at: indexPath, item: item)
    }
